create table ram(
    ram_id bigint(20) unsigned not null AUTO_INCREMENT,
    ram_fullname varchar(50) not null,
    ram_type_of_memory varchar(50) not null,
    ram_total_capacity varchar(50) not null,
    ram_capacity varchar(50) not null,
    ram_frequency varchar(50) not null,
    ram_cycle_latency varchar(50) not null,
    ram_price int(20) unsigned not null,
    UNIQUE KEY(ram_id))
    ENGINE=INNODB DEFAULT charset=utf8;




